import { BASE_URL } from '../constants';

const post = book => {
  const data = JSON.stringify({ book: { title: book.title, category: book.category } });
  console.log('IN POST', data);
  return fetch(
    '/books',
    {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: data,
    },
  );
};

export default post;
