import getToken from '../helpers/getToken';

const post = book => {
  const token = getToken();
  const data = JSON.stringify(book, token);

  return fetch(
    '/books',
    {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: data,
    },
  );
};

export default post;
